/**
 * Callback related
 * @version 1.0.0
 */
declare module './callback';
export declare const giveAndDo: <T>(n: (...arg: any[]) => T) => T;
export declare const giveAndReturn: <T>(n: T) => T;
export declare const cbNoArg: <T>(res: () => any, rej: (err: T) => any) => (err: T) => any;
export declare const cbOneArg: <T, N>(res: (n: N) => any, rej: (err: T) => any) => (err: T, n: N) => any;
export declare const cbArgs: <T, N extends any[]>(hdl: (...arg: N) => any, res: () => any, rej: (err: T) => any) => (err: T, ...arg: N) => any;
